<?php
class installerOct {
	static public $update_to_version_method = '';
	static private $_firstTimeActivated = false;
	static public function init() {
		global $wpdb;
		$wpPrefix = $wpdb->prefix; /* add to 0.0.3 Versiom */
		require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
		$current_version = get_option($wpPrefix. OCT_DB_PREF. 'db_version', 0);
		if(!$current_version)
			self::$_firstTimeActivated = true;
		/**
		 * modules 
		 */
		dbOct::query("DROP TABLE @__modules");
		if (!dbOct::exist("@__modules")) {
			dbDelta(dbOct::prepareQuery("CREATE TABLE IF NOT EXISTS `@__modules` (
			  `id` smallint(3) NOT NULL AUTO_INCREMENT,
			  `code` varchar(32) NOT NULL,
			  `active` tinyint(1) NOT NULL DEFAULT '0',
			  `type_id` tinyint(1) NOT NULL DEFAULT '0',
			  `label` varchar(64) DEFAULT NULL,
			  `ex_plug_dir` varchar(255) DEFAULT NULL,
			  PRIMARY KEY (`id`),
			  UNIQUE INDEX `code` (`code`)
			) DEFAULT CHARSET=utf8;"));
			dbOct::query("INSERT INTO `@__modules` (id, code, active, type_id, label) VALUES
				(NULL, 'adminmenu',1,1,'Admin Menu'),
				(NULL, 'options',1,1,'Options'),
				(NULL, 'user',1,1,'Users'),
				(NULL, 'pages',1,1,'Pages'),
				(NULL, 'templates',1,1,'templates'),
				(NULL, 'supsystic_promo',1,1,'supsystic_promo'),
				(NULL, 'admin_nav',1,1,'admin_nav'),
				
				(NULL, 'octo',1,1,'octo'),
				(NULL, 'subscribe',1,1,'subscribe'),
				(NULL, 'mail',1,1,'mail');");
		}
		/**
		 *  modules_type 
		 */
		if(!dbOct::exist("@__modules_type")) {
			dbDelta(dbOct::prepareQuery("CREATE TABLE IF NOT EXISTS `@__modules_type` (
			  `id` smallint(3) NOT NULL AUTO_INCREMENT,
			  `label` varchar(32) NOT NULL,
			  PRIMARY KEY (`id`)
			) AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;"));
			dbOct::query("INSERT INTO `@__modules_type` VALUES
				(1,'system'),
				(6,'addons');");
		}
		//dbOct::query("DROP TABLE @__octo");
		//dbOct::query("DROP TABLE @__octo_blocks");
		dbOct::query("DROP TABLE @__octo_blocks_categories");
		/**
		 * Octo main table
		 */
		if (!dbOct::exist("@__octo")) {
			dbDelta(dbOct::prepareQuery("CREATE TABLE IF NOT EXISTS `@__octo` (
				`id` INT(11) NOT NULL AUTO_INCREMENT,
				`pid`  INT(11) NOT NULL,
				`active` TINYINT(1) NOT NULL DEFAULT '1',
				`date_created` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
				PRIMARY KEY (`id`)
			) DEFAULT CHARSET=utf8;"));
		}
		if (!dbOct::exist("@__octo_blocks")) {
			dbDelta(dbOct::prepareQuery("CREATE TABLE IF NOT EXISTS `@__octo_blocks` (
				`id` INT(11) NOT NULL AUTO_INCREMENT,
				`oid`  INT(11) NOT NULL DEFAULT '0',
				`cid`  INT(11) NOT NULL DEFAULT '0',
				`unique_id` varchar(8) NOT NULL,
				`label` varchar(128) NOT NULL,
				`original_id` INT(11) NOT NULL DEFAULT '0',
				`params` TEXT NOT NULL,
				`html` TEXT NOT NULL,
				`css` TEXT NOT NULL,
				`img` varchar(64) DEFAULT NULL,
				`sort_order` MEDIUMINT(5) NOT NULL DEFAULT '0',
				`is_base` TINYINT(1) NOT NULL DEFAULT '1',
				`date_created` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
				PRIMARY KEY (`id`)
			) DEFAULT CHARSET=utf8;"));
		}
		if (!dbOct::exist("@__octo_blocks_categories")) {
			dbDelta(dbOct::prepareQuery("CREATE TABLE IF NOT EXISTS `@__octo_blocks_categories` (
				`id` INT(11) NOT NULL AUTO_INCREMENT,
				`code` varchar(32) NOT NULL,
				`label` varchar(256) NOT NULL,
				PRIMARY KEY (`id`)
			) DEFAULT CHARSET=utf8;"));
		}
		if (!dbOct::exist("@__octo", "is_pro")) {
			dbOct::query("ALTER TABLE @__octo
				ADD COLUMN `unique_id`  varchar(8) NOT NULL AFTER `id`,
				ADD COLUMN `label`  varchar(128) NOT NULL AFTER `unique_id`,
				ADD COLUMN `original_id`  int(11) NOT NULL AFTER `active`,
				ADD COLUMN `is_base`  tinyint(1) NOT NULL AFTER `original_id`,
				ADD COLUMN `img`  varchar(64) NOT NULL AFTER `is_base`,
				ADD COLUMN `sort_order`  mediumint(5) NOT NULL AFTER `img`,
				ADD COLUMN `params`  text NOT NULL AFTER `sort_order`,
				ADD COLUMN `is_pro`  tinyint(1) NOT NULL AFTER `params`");
			dbOct::query("
				ALTER TABLE @__octo_blocks
				ADD COLUMN `is_pro`  tinyint(1) NOT NULL AFTER `date_created`");
		}
		self::initBaseBlocksCategories();
		self::initBaseBlocks();
		self::initBaseTemplates();
		/**
		* Plugin usage statistics
		*/
		if(!dbOct::exist("@__usage_stat")) {
			dbDelta(dbOct::prepareQuery("CREATE TABLE `@__usage_stat` (
			  `id` int(11) NOT NULL AUTO_INCREMENT,
			  `code` varchar(64) NOT NULL,
			  `visits` int(11) NOT NULL DEFAULT '0',
			  `spent_time` int(11) NOT NULL DEFAULT '0',
			  `modify_timestamp` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
			  UNIQUE INDEX `code` (`code`),
			  PRIMARY KEY (`id`)
			) DEFAULT CHARSET=utf8"));
			dbOct::query("INSERT INTO `@__usage_stat` (code, visits) VALUES ('installed', 1)");
		}
		/**
		 * Subscribers
		 */
		if (!dbOct::exist("@__subscribers")) {
			  dbDelta(dbOct::prepareQuery("CREATE TABLE `@__subscribers` (
				`id` int(11) NOT NULL AUTO_INCREMENT,
				`username` VARCHAR(128) NULL DEFAULT NULL,
				`email` VARCHAR(128) NOT NULL,
				`hash` VARCHAR(128) NOT NULL,
				`activated` TINYINT(1) NOT NULL DEFAULT '0',
				`block_id` int(11) NOT NULL DEFAULT '0',
				`date_created` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
				`all_data` TEXT NOT NULL,
				PRIMARY KEY (`id`)
			  ) DEFAULT CHARSET=utf8;"));
		}

		if (!frameOct::_()->getTable('modules')->exists('contact', 'code'))
			frameOct::_()->getTable('modules')->insert(array(
				'code' => 'contact',
				'active' => 1,
				'type_id' => 1,
				'label' => 'contact'
			));

		installerDbUpdaterOct::runUpdate();
		if($current_version && !self::$_firstTimeActivated) {
			self::setUsed();
		}
		update_option($wpPrefix. OCT_DB_PREF. 'db_version', OCT_VERSION);
		add_option($wpPrefix. OCT_DB_PREF. 'db_installed', 1);
	}
	static public function setUsed() {
		update_option(OCT_DB_PREF. 'plug_was_used', 1);
	}
	static public function isUsed() {
		// No welcome page for now
		return true;
		return (int) get_option(OCT_DB_PREF. 'plug_was_used');
	}
	static public function delete() {
		global $wpdb;
		$wpPrefix = $wpdb->prefix;
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.OCT_DB_PREF."modules`");
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.OCT_DB_PREF."modules_type`");
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.OCT_DB_PREF."usage_stat`");
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.OCT_DB_PREF."octo`");
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.OCT_DB_PREF."octo_blocks`");
		delete_option($wpPrefix. OCT_DB_PREF. 'db_version');
		delete_option($wpPrefix. OCT_DB_PREF. 'db_installed');
	}
	static public function update() {
		global $wpdb;
		$wpPrefix = $wpdb->prefix; /* add to 0.0.3 Versiom */
		$currentVersion = get_option($wpPrefix. OCT_DB_PREF. 'db_version', 0);

		if(!$currentVersion || version_compare(OCT_VERSION, $currentVersion, '>')) {
			self::init();
			update_option($wpPrefix. OCT_DB_PREF. 'db_version', OCT_VERSION);
		}
	}
