	static public function bindOctoToTpls($oid, $uniqueIds) {
		if(!is_numeric($oid)) {
			$oid = dbOct::get('SELECT id FROM @__octo WHERE unique_id = "'. $oid. '" AND original_id = 0 AND is_base = 1', 'one');
		}
		dbOct::query('UPDATE @__octo_blocks SET oid = '. $oid. ' WHERE unique_id IN ("'. implode('","', $uniqueIds). '") AND original_id = 0 AND is_base = 1');
	}

	static public function installDataByUid($tbl, $uid, $data) {
		$id = (int) dbOct::get("SELECT id FROM $tbl WHERE unique_id = '$uid' AND original_id = 0 AND is_base = 1", 'one');
		$action = $id ? 'UPDATE' : 'INSERT INTO';
		$values = array();
		foreach($data as $k => $v) {
			$values[] = "$k = \"$v\"";
		}
		$valuesStr = implode(',', $values);
		$query = "$action $tbl SET $valuesStr";
		if($action == 'UPDATE')
			$query .= " WHERE unique_id = '$uid' AND original_id = 0 AND is_base = 1";
		if(dbOct::query($query)) {
			return $action == 'UPDATE' ? $id : dbOct::insertID();
		}
		return false;
	}
